"public class Fruit implements Parcelable {
    String mColor = "Green";

    // ...

    protected Fruit(Parcel in) {
        mColor = in.readString();
    }

    @Override
    public void writeToParcel(Parcel dest, int flags) {
        dest.writeString(mColor);
    }

    // ...

}
